import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';

export default function FavList() {
  const [data, setData] = useState([]);

  // Fetch data from the database
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3044/addmovie'); // Replace '/api/data' with your actual API endpoint
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleUpdate = async (id) => {
    // Perform update operation
    try {
      // Update the data in the database using an API call
      await axios.put(`http://localhost:3044/addmovie${id}`, { /* Updated data */ });

      // Fetch updated data
      fetchData();
    } catch (error) {
      console.error('Error updating data:', error);
    }
  };

  const handleDelete = async (id) => {
    // Perform delete operation
    try {
      // Delete the data from the database using an API call
      await axios.delete(`http://localhost:3044/addmovie${id}`);

      // Fetch updated data
      fetchData();
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  return (
    <div>
      {data.map((item) => (
        <Card key={item.id}>
          <Card.Body>
            <Card.Title>{item.title}</Card.Title>
            <Card.Text>{item.description}</Card.Text>
            <Button variant="primary" onClick={() => handleUpdate(item.id)}>
              Update
            </Button>{' '}
            <Button variant="danger" onClick={() => handleDelete(item.id)}>
              Delete
            </Button>
          </Card.Body>
        </Card>
      ))}
    </div>
  );
}
