import React from 'react';
import Component1 from './Component1';
import Component2 from './Component2';
import Component3 from './Component3';
import ModalMovie from './ModalMovie';

const Movie = ({ movie }) => {
  const addToFavorites = () => {
    // Logic to add the movie to the favorite list
  };

  return (
    <div key={movie.id} className="movie-card">
      <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
      <h2>{movie.title}</h2>
      <p>{movie.overview}</p>
      <button onClick={addToFavorites}>Add to Favorites</button>
      <ModalMovie movie={movie} /> {/* Render ModalMovie */}
      <Component1 movieId={movie.id} /> {/* Pass movie ID as a prop */}
      <Component2 movieId={movie.id} /> {/* Pass movie ID as a prop */}
      <Component3 movieId={movie.id} /> {/* Pass movie ID as a prop */}
    </div>
  );
};

const MovieList = ({ movies }) => {
  return (
    <div className="movie-list">
      {movies.map((movie) => (
        <Movie movie={movie} key={movie.id} />
      ))}
    </div>
  );
};

export default MovieList;
