import React, { useState } from 'react';
import axios from 'axios';
import './mod.css';

const ModalMovie = ({ movie }) => {
  const [showModal, setShowModal] = useState(false);
  const [comment, setComment] = useState('');

  const handleAddToFavorites = () => {
    axios
      .post('http://localhost:3044/addmovie', {
        movieData: {
          name: movie.title,
          img: movie.poster_path,
          comment: comment,
        },
      })
      .then(response => {
        console.log('Movie added to favorites:', response.data);
        // Add any additional logic here, such as displaying a success message
      })
      .catch(error => {
        console.error('Error adding movie to favorites:', error);
        // Handle any errors that occur during the POST request
      });
  };

  const toggleModal = () => {
    if (!showModal) {
      setShowModal(true);
    }
  };

  const closeModal = () => {
    setShowModal(false);
  };
  return (
    <>
      {!showModal && (
        <button onClick={toggleModal}>View Details</button>
      )}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{movie.title}</h2>
            <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
            <p>Add a comment:</p>
            <textarea
              value={comment}
              onChange={e => setComment(e.target.value)}
              rows="4"
              cols="50"
            />
            <button onClick={handleAddToFavorites}>Add to Favorites</button>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </>
  );
};

export default ModalMovie;
